-- Create user profiles table
create table public.user_profiles (
  id uuid primary key references auth.users on delete cascade,
  name text not null,
  email text not null,
  phone text,
  points integer default 0 not null,
  profile_image text,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Enable RLS
alter table public.user_profiles enable row level security;

-- Create RLS policies
create policy "Users can view their own profile"
  on public.user_profiles for select
  using (auth.uid() = id);

create policy "Users can update their own profile"
  on public.user_profiles for update
  using (auth.uid() = id);

create policy "Users can insert their own profile"
  on public.user_profiles for insert
  with check (auth.uid() = id);

-- Create index for performance
create index user_profiles_email_idx on public.user_profiles(email);
create index user_profiles_points_idx on public.user_profiles(points);

-- Create orders table
create table public.orders (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references public.user_profiles(id) on delete cascade not null,
  total numeric(10, 2) not null,
  points_earned integer not null default 0,
  status text not null default 'pending' check (status in ('pending', 'preparing', 'ready', 'completed', 'cancelled')),
  delivery_address text,
  pickup_notes text,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Create order items table
create table public.order_items (
  id uuid primary key default gen_random_uuid(),
  order_id uuid references public.orders(id) on delete cascade not null,
  menu_item_id uuid references public.menu_items(id) on delete set null,
  name text not null,
  price numeric(10, 2) not null,
  quantity integer not null,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Enable RLS
alter table public.orders enable row level security;
alter table public.order_items enable row level security;

-- Create RLS policies for orders
create policy "Users can view their own orders"
  on public.orders for select
  using (auth.uid() = user_id);

create policy "Users can insert their own orders"
  on public.orders for insert
  with check (auth.uid() = user_id);

create policy "Users can update their own orders"
  on public.orders for update
  using (auth.uid() = user_id);

-- Create RLS policies for order items
create policy "Users can view their own order items"
  on public.order_items for select
  using (
    exists (
      select 1 from public.orders
      where orders.id = order_items.order_id
      and orders.user_id = auth.uid()
    )
  );

create policy "Users can insert their own order items"
  on public.order_items for insert
  with check (
    exists (
      select 1 from public.orders
      where orders.id = order_items.order_id
      and orders.user_id = auth.uid()
    )
  );

-- Create indexes
create index orders_user_id_idx on public.orders(user_id);
create index orders_status_idx on public.orders(status);
create index orders_created_at_idx on public.orders(created_at desc);
create index order_items_order_id_idx on public.order_items(order_id);

-- Create gift cards table
create table public.gift_cards (
  id uuid primary key default gen_random_uuid(),
  sender_id uuid references public.user_profiles(id) on delete cascade not null,
  recipient_id uuid references public.user_profiles(id) on delete cascade,
  recipient_email text,
  recipient_name text,
  points integer not null,
  message text,
  status text not null default 'pending' check (status in ('pending', 'sent', 'redeemed', 'expired')),
  redeemed_at timestamp with time zone,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Enable RLS
alter table public.gift_cards enable row level security;

-- Create RLS policies
create policy "Users can view gift cards they sent"
  on public.gift_cards for select
  using (auth.uid() = sender_id);

create policy "Users can view gift cards they received"
  on public.gift_cards for select
  using (auth.uid() = recipient_id);

create policy "Users can insert gift cards"
  on public.gift_cards for insert
  with check (auth.uid() = sender_id);

create policy "Users can update gift cards they received"
  on public.gift_cards for update
  using (auth.uid() = recipient_id);

-- Create indexes
create index gift_cards_sender_id_idx on public.gift_cards(sender_id);
create index gift_cards_recipient_id_idx on public.gift_cards(recipient_id);
create index gift_cards_status_idx on public.gift_cards(status);

-- Create events table
create table public.events (
  id uuid primary key default gen_random_uuid(),
  title text not null,
  description text not null,
  date timestamp with time zone not null,
  location text not null,
  capacity integer not null,
  image text not null,
  is_private boolean default false,
  is_invite_only boolean default false,
  shareable_link text unique,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Create event RSVPs table
create table public.event_rsvps (
  id uuid primary key default gen_random_uuid(),
  event_id uuid references public.events(id) on delete cascade not null,
  user_id uuid references public.user_profiles(id) on delete cascade not null,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  unique(event_id, user_id)
);

-- Enable RLS
alter table public.events enable row level security;
alter table public.event_rsvps enable row level security;

-- Create RLS policies for events
create policy "Anyone can view public events"
  on public.events for select
  using (is_private = false and is_invite_only = false);

create policy "Authenticated users can view private events"
  on public.events for select
  using (auth.role() = 'authenticated' and is_private = true and is_invite_only = false);

create policy "Anyone with link can view invite-only events"
  on public.events for select
  using (is_invite_only = true);

create policy "Only authenticated users can insert events"
  on public.events for insert
  with check (auth.role() = 'authenticated');

create policy "Only authenticated users can update events"
  on public.events for update
  using (auth.role() = 'authenticated');

create policy "Only authenticated users can delete events"
  on public.events for delete
  using (auth.role() = 'authenticated');

-- Create RLS policies for event RSVPs
create policy "Users can view their own RSVPs"
  on public.event_rsvps for select
  using (auth.uid() = user_id);

create policy "Users can view RSVPs for events they created"
  on public.event_rsvps for select
  using (auth.role() = 'authenticated');

create policy "Users can insert their own RSVPs"
  on public.event_rsvps for insert
  with check (auth.uid() = user_id);

create policy "Users can delete their own RSVPs"
  on public.event_rsvps for delete
  using (auth.uid() = user_id);

-- Create indexes
create index events_date_idx on public.events(date);
create index events_is_private_idx on public.events(is_private);
create index events_is_invite_only_idx on public.events(is_invite_only);
create index events_shareable_link_idx on public.events(shareable_link);
create index event_rsvps_event_id_idx on public.event_rsvps(event_id);
create index event_rsvps_user_id_idx on public.event_rsvps(user_id);

-- Create payment methods table
create table public.payment_methods (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references public.user_profiles(id) on delete cascade not null,
  type text not null check (type in ('credit', 'debit')),
  card_number text not null,
  cardholder_name text not null,
  expiry_date text not null,
  is_default boolean default false,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Enable RLS
alter table public.payment_methods enable row level security;

-- Create RLS policies
create policy "Users can view their own payment methods"
  on public.payment_methods for select
  using (auth.uid() = user_id);

create policy "Users can insert their own payment methods"
  on public.payment_methods for insert
  with check (auth.uid() = user_id);

create policy "Users can update their own payment methods"
  on public.payment_methods for update
  using (auth.uid() = user_id);

create policy "Users can delete their own payment methods"
  on public.payment_methods for delete
  using (auth.uid() = user_id);

-- Create indexes
create index payment_methods_user_id_idx on public.payment_methods(user_id);
create index payment_methods_is_default_idx on public.payment_methods(is_default);

-- Create notifications table
create table public.notifications (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references public.user_profiles(id) on delete cascade not null,
  title text not null,
  message text not null,
  type text not null check (type in ('special', 'event', 'order', 'general', 'giftcard')),
  read boolean default false,
  action_url text,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Create theme settings table
create table public.theme_settings (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references public.user_profiles(id) on delete cascade not null unique,
  mode text not null default 'auto' check (mode in ('light', 'dark', 'auto')),
  color_scheme text not null default 'default' check (color_scheme in ('default', 'warm', 'cool', 'vibrant', 'minimal')),
  created_at timestamp with time zone default timezone('utc'::text, now()) not null,
  updated_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Enable RLS
alter table public.notifications enable row level security;
alter table public.theme_settings enable row level security;

-- Create RLS policies for notifications
create policy "Users can view their own notifications"
  on public.notifications for select
  using (auth.uid() = user_id);

create policy "Users can insert their own notifications"
  on public.notifications for insert
  with check (auth.uid() = user_id);

create policy "Users can update their own notifications"
  on public.notifications for update
  using (auth.uid() = user_id);

create policy "Users can delete their own notifications"
  on public.notifications for delete
  using (auth.uid() = user_id);

-- Create RLS policies for theme settings
create policy "Users can view their own theme settings"
  on public.theme_settings for select
  using (auth.uid() = user_id);

create policy "Users can insert their own theme settings"
  on public.theme_settings for insert
  with check (auth.uid() = user_id);

create policy "Users can update their own theme settings"
