      .single();

    if (eventError || !event) {
      return new Response(
        JSON.stringify({ error: 'Event not found' }),
        { status: 404, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check current RSVPs
    const { data: rsvps, error: rsvpsError } = await supabaseClient
      .from('event_rsvps')
      .select('id')
      .eq('event_id', eventId);

    if (rsvpsError) {
      console.error('RSVPs fetch error:', rsvpsError);
      return new Response(
        JSON.stringify({ error: 'Failed to check event capacity' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    if (rsvps && rsvps.length >= event.capacity) {
      return new Response(
        JSON.stringify({ error: 'Event is at full capacity' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check if user already RSVP'd
    const { data: existingRsvp } = await supabaseClient
      .from('event_rsvps')
      .select('id')
      .eq('event_id', eventId)
      .eq('user_id', user.id)
      .single();

    if (existingRsvp) {
      return new Response(
        JSON.stringify({ error: 'Already RSVP\'d to this event' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Create RSVP
    const { data: rsvp, error: rsvpError } = await supabaseClient
      .from('event_rsvps')
      .insert({
        event_id: eventId,
        user_id: user.id,
      })
      .select()
      .single();

    if (rsvpError) {
      console.error('RSVP creation error:', rsvpError);
      return new Response(
        JSON.stringify({ error: 'Failed to create RSVP' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Create notification
    await supabaseClient
      .from('notifications')
      .insert({
        user_id: user.id,
        title: 'Event RSVP Confirmed',
        message: `You have successfully RSVP'd to ${event.title}`,
        type: 'event',
      });

    return new Response(
      JSON.stringify({ 
        success: true, 
        rsvp 
      }),
      { status: 200, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error:', error);
    return new Response(
      JSON.stringify({ error: 'Internal server error' }),
      { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  }
});

