-- Enable realtime for orders table
ALTER PUBLICATION supabase_realtime ADD TABLE orders;

-- Create function to broadcast order changes
CREATE OR REPLACE FUNCTION broadcast_order_changes()
RETURNS TRIGGER AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'order:' || COALESCE(NEW.user_id, OLD.user_id)::text,
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN COALESCE(NEW, OLD);
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Create trigger for order changes
DROP TRIGGER IF EXISTS orders_realtime_trigger ON orders;
CREATE TRIGGER orders_realtime_trigger
  AFTER INSERT OR UPDATE OR DELETE ON orders
  FOR EACH ROW EXECUTE FUNCTION broadcast_order_changes();

-- Create RLS policy for realtime.messages (for reading order updates)
CREATE POLICY "users_can_read_own_order_updates" ON realtime.messages
  FOR SELECT TO authenticated
  USING (
    topic LIKE 'order:%' AND
    SPLIT_PART(topic, ':', 2)::uuid = auth.uid()
  );

-- Create RLS policy for realtime.messages (for writing order updates)
CREATE POLICY "system_can_write_order_updates" ON realtime.messages
  FOR INSERT TO authenticated
  WITH CHECK (
    topic LIKE 'order:%'
  );
