import { createClient } from 'https://esm.sh/@supabase/supabase-js@2.80.0';
import 'jsr:@supabase/functions-js/edge-runtime.d.ts';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

Deno.serve(async (req: Request) => {
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_ANON_KEY') ?? '',
      {
        global: {
          headers: { Authorization: req.headers.get('Authorization')! },
        },
      }
    );

    const { data: { user }, error: userError } = await supabaseClient.auth.getUser();
    if (userError || !user) {
      return new Response(
        JSON.stringify({ error: 'Unauthorized' }),
        { status: 401, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    const { items, deliveryAddress, pickupNotes } = await req.json();

    if (!items || items.length === 0) {
      return new Response(
        JSON.stringify({ error: 'No items in order' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Calculate total and points
    const total = items.reduce((sum: number, item: any) => sum + (item.price * item.quantity), 0);
    const pointsEarned = Math.floor(total); // 1 point per dollar

    // Create order
    const { data: order, error: orderError } = await supabaseClient
      .from('orders')
      .insert({
        user_id: user.id,
        total,
        points_earned: pointsEarned,
        status: 'pending',
        delivery_address: deliveryAddress,
        pickup_notes: pickupNotes,
      })
      .select()
      .single();

    if (orderError) {
      console.error('Order creation error:', orderError);
      return new Response(
        JSON.stringify({ error: 'Failed to create order' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Create order items
    const orderItems = items.map((item: any) => ({
      order_id: order.id,
      menu_item_id: item.id,
      name: item.name,
      price: item.price,
      quantity: item.quantity,
    }));

    const { error: itemsError } = await supabaseClient
      .from('order_items')
      .insert(orderItems);

    if (itemsError) {
      console.error('Order items creation error:', itemsError);
      return new Response(
        JSON.stringify({ error: 'Failed to create order items' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Update user points
    const { error: pointsError } = await supabaseClient
      .from('user_profiles')
      .update({ points: supabaseClient.rpc('increment_points', { user_id: user.id, amount: pointsEarned }) })
      .eq('id', user.id);

    // Create notification
    await supabaseClient
      .from('notifications')
      .insert({
        user_id: user.id,
        title: 'Order Placed',
        message: `Your order of $${total.toFixed(2)} has been placed successfully. You earned ${pointsEarned} points!`,
        type: 'order',
      });

    return new Response(
      JSON.stringify({ 
        success: true, 
        order: { ...order, items: orderItems },
        pointsEarned 
      }),
      { status: 200, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error:', error);
    return new Response(
      JSON.stringify({ error: 'Internal server error' }),
      { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  }
});

import { createClient } from 'https://esm.sh/@supabase/supabase-js@2.80.0';
import 'jsr:@supabase/functions-js/edge-runtime.d.ts';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

Deno.serve(async (req: Request) => {
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_ANON_KEY') ?? '',
      {
        global: {
          headers: { Authorization: req.headers.get('Authorization')! },
        },
      }
    );

    const { data: { user }, error: userError } = await supabaseClient.auth.getUser();
    if (userError || !user) {
      return new Response(
        JSON.stringify({ error: 'Unauthorized' }),
        { status: 401, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    const { recipientId, recipientEmail, recipientName, points, message } = await req.json();

    if (!points || points <= 0) {
      return new Response(
        JSON.stringify({ error: 'Invalid points amount' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check sender has enough points
    const { data: senderProfile, error: senderError } = await supabaseClient
      .from('user_profiles')
      .select('points, name')
      .eq('id', user.id)
      .single();

    if (senderError || !senderProfile) {
      return new Response(
        JSON.stringify({ error: 'Failed to fetch sender profile' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    if (senderProfile.points < points) {
      return new Response(
        JSON.stringify({ error: 'Insufficient points' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Deduct points from sender
    const { error: deductError } = await supabaseClient
      .from('user_profiles')
      .update({ points: senderProfile.points - points })
      .eq('id', user.id);

    if (deductError) {
      console.error('Points deduction error:', deductError);
      return new Response(
        JSON.stringify({ error: 'Failed to deduct points' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Create gift card
    const { data: giftCard, error: giftCardError } = await supabaseClient
      .from('gift_cards')
      .insert({
        sender_id: user.id,
        recipient_id: recipientId,
        recipient_email: recipientEmail,
        recipient_name: recipientName,
        points,
        message,
        status: recipientId ? 'sent' : 'pending',
      })
      .select()
      .single();

    if (giftCardError) {
      console.error('Gift card creation error:', giftCardError);
      // Rollback points
      await supabaseClient
        .from('user_profiles')
        .update({ points: senderProfile.points })
        .eq('id', user.id);
      return new Response(
        JSON.stringify({ error: 'Failed to create gift card' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // If recipient exists, add points and create notification
    if (recipientId) {
      const { data: recipientProfile } = await supabaseClient
        .from('user_profiles')
        .select('points')
        .eq('id', recipientId)
        .single();

      if (recipientProfile) {
        await supabaseClient
          .from('user_profiles')
          .update({ points: recipientProfile.points + points })
          .eq('id', recipientId);

        await supabaseClient
          .from('notifications')
          .insert({
            user_id: recipientId,
            title: 'Gift Card Received',
            message: `${senderProfile.name} sent you ${points} points! ${message ? message : ''}`,
            type: 'giftcard',
          });
      }
    }

    return new Response(
      JSON.stringify({ 
        success: true, 
        giftCard 
      }),
      { status: 200, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error:', error);
    return new Response(
      JSON.stringify({ error: 'Internal server error' }),
      { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  }
});

import { createClient } from 'https://esm.sh/@supabase/supabase-js@2.80.0';
import 'jsr:@supabase/functions-js/edge-runtime.d.ts';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

Deno.serve(async (req: Request) => {
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_ANON_KEY') ?? '',
      {
        global: {
          headers: { Authorization: req.headers.get('Authorization')! },
        },
      }
    );

    const { data: { user }, error: userError } = await supabaseClient.auth.getUser();
    if (userError || !user) {
      return new Response(
        JSON.stringify({ error: 'Unauthorized' }),
        { status: 401, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    const { merchId, merchName, pointsCost, deliveryAddress, pickupNotes } = await req.json();

    if (!merchId || !pointsCost) {
      return new Response(
        JSON.stringify({ error: 'Invalid merch data' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check user has enough points
    const { data: userProfile, error: profileError } = await supabaseClient
      .from('user_profiles')
      .select('points')
      .eq('id', user.id)
      .single();

    if (profileError || !userProfile) {
      return new Response(
        JSON.stringify({ error: 'Failed to fetch user profile' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    if (userProfile.points < pointsCost) {
      return new Response(
        JSON.stringify({ error: 'Insufficient points' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check merch is in stock
    const { data: merchItem, error: merchError } = await supabaseClient
      .from('merch_items')
      .select('in_stock')
      .eq('id', merchId)
      .single();

    if (merchError || !merchItem || !merchItem.in_stock) {
      return new Response(
        JSON.stringify({ error: 'Merch item not available' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Deduct points from user
    const { error: deductError } = await supabaseClient
      .from('user_profiles')
      .update({ points: userProfile.points - pointsCost })
      .eq('id', user.id);

    if (deductError) {
      console.error('Points deduction error:', deductError);
      return new Response(
        JSON.stringify({ error: 'Failed to deduct points' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Create merch redemption
    const { data: redemption, error: redemptionError } = await supabaseClient
      .from('merch_redemptions')
      .insert({
        user_id: user.id,
        merch_item_id: merchId,
        merch_name: merchName,
        points_cost: pointsCost,
        delivery_address: deliveryAddress,
        pickup_notes: pickupNotes,
        status: 'pending',
      })
      .select()
      .single();

    if (redemptionError) {
      console.error('Redemption creation error:', redemptionError);
      // Rollback points
      await supabaseClient
        .from('user_profiles')
        .update({ points: userProfile.points })
        .eq('id', user.id);
      return new Response(
        JSON.stringify({ error: 'Failed to create redemption' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Create notification
    await supabaseClient
      .from('notifications')
      .insert({
        user_id: user.id,
        title: 'Merch Redeemed',
        message: `You redeemed ${merchName} for ${pointsCost} points!`,
        type: 'general',
      });

    return new Response(
      JSON.stringify({ 
        success: true, 
        redemption 
      }),
      { status: 200, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error:', error);
    return new Response(
      JSON.stringify({ error: 'Internal server error' }),
      { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  }
});

import { createClient } from 'https://esm.sh/@supabase/supabase-js@2.80.0';
import 'jsr:@supabase/functions-js/edge-runtime.d.ts';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

Deno.serve(async (req: Request) => {
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_ANON_KEY') ?? '',
      {
        global: {
          headers: { Authorization: req.headers.get('Authorization')! },
        },
      }
    );

    const { data: { user }, error: userError } = await supabaseClient.auth.getUser();
    if (userError || !user) {
      return new Response(
        JSON.stringify({ error: 'Unauthorized' }),
        { status: 401, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    const { eventId } = await req.json();

    if (!eventId) {
      return new Response(
        JSON.stringify({ error: 'Event ID is required' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check if event exists and has capacity
    const { data: event, error: eventError } = await supabaseClient
      .from('events')
      .select('id, title, capacity')
      .eq('id', eventId)
      .single();

    if (eventError || !event) {
      return new Response(
        JSON.stringify({ error: 'Event not found' }),
        { status: 404, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check current RSVPs
    const { data: rsvps, error: rsvpsError } = await supabaseClient
      .from('event_rsvps')
      .select('id')
      .eq('event_id', eventId);

    if (rsvpsError) {
      console.error('RSVPs fetch error:', rsvpsError);
      return new Response(
        JSON.stringify({ error: 'Failed to check event capacity' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    if (rsvps && rsvps.length >= event.capacity) {
      return new Response(
        JSON.stringify({ error: 'Event is at full capacity' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check if user already RSVP'd
    const { data: existingRsvp } = await supabaseClient
      .from('event_rsvps')
      .select('id')
      .eq('event_id', eventId)
      .eq('user_id', user.id)
      .single();

    if (existingRsvp) {
      return new Response(
        JSON.stringify({ error: 'Already RSVP\'d to this event' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Create RSVP
    const { data: rsvp, error: rsvpError } = await supabaseClient
      .from('event_rsvps')
      .insert({
        event_id: eventId,
        user_id: user.id,
      })
      .select()
      .single();

    if (rsvpError) {
      console.error('RSVP creation error:', rsvpError);
      return new Response(
        JSON.stringify({ error: 'Failed to create RSVP' }),
        { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Create notification
    await supabaseClient
      .from('notifications')
      .insert({
        user_id: user.id,
        title: 'Event RSVP Confirmed',
        message: `You have successfully RSVP'd to ${event.title}`,
        type: 'event',
      });

    return new Response(
      JSON.stringify({ 
        success: true, 
        rsvp 
      }),
      { status: 200, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error:', error);
    return new Response(
      JSON.stringify({ error: 'Internal server error' }),
      { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  }
});

