-- Enable realtime for orders table
ALTER PUBLICATION supabase_realtime ADD TABLE orders;

-- Create function to broadcast order changes
CREATE OR REPLACE FUNCTION broadcast_order_changes()
RETURNS TRIGGER AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'order:' || COALESCE(NEW.user_id, OLD.user_id)::text,
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN COALESCE(NEW, OLD);
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Create trigger for order changes
DROP TRIGGER IF EXISTS orders_realtime_trigger ON orders;
CREATE TRIGGER orders_realtime_trigger
  AFTER INSERT OR UPDATE OR DELETE ON orders
  FOR EACH ROW EXECUTE FUNCTION broadcast_order_changes();

-- Create RLS policy for realtime.messages (for reading order updates)
CREATE POLICY "users_can_read_own_order_updates" ON realtime.messages
  FOR SELECT TO authenticated
  USING (
    topic LIKE 'order:%' AND
    SPLIT_PART(topic, ':', 2)::uuid = auth.uid()
  );

-- Create RLS policy for realtime.messages (for writing order updates)
CREATE POLICY "system_can_write_order_updates" ON realtime.messages
  FOR INSERT TO authenticated
  WITH CHECK (
    topic LIKE 'order:%'
  );

-- Create a helper function to check if the current user is an admin
CREATE OR REPLACE FUNCTION is_admin()
RETURNS BOOLEAN AS $$
BEGIN
  RETURN EXISTS (
    SELECT 1 FROM user_profiles
    WHERE user_id = auth.uid()
    AND is_admin = true
  );
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Grant execute permission to authenticated users
GRANT EXECUTE ON FUNCTION is_admin() TO authenticated;

-- Enable RLS on reservations table
ALTER TABLE reservations ENABLE ROW LEVEL SECURITY;

-- Create RLS policies for reservations
-- Admin users can view all reservations
CREATE POLICY "Admins can view all reservations"
  ON reservations
  FOR SELECT
  USING (is_admin());

-- Admin users can insert reservations
CREATE POLICY "Admins can insert reservations"
  ON reservations
  FOR INSERT
  WITH CHECK (is_admin());

-- Admin users can update reservations
CREATE POLICY "Admins can update reservations"
  ON reservations
  FOR UPDATE
  USING (is_admin());

-- Admin users can delete reservations
CREATE POLICY "Admins can delete reservations"
  ON reservations
  FOR DELETE
  USING (is_admin());

-- Add status column to reservations table if it doesn't exist
DO $$ 
BEGIN
  IF NOT EXISTS (
    SELECT 1 FROM information_schema.columns 
    WHERE table_name = 'reservations' AND column_name = 'status'
  ) THEN
    ALTER TABLE reservations ADD COLUMN status TEXT DEFAULT 'pending' CHECK (status IN ('pending', 'confirmed', 'cancelled', 'completed', 'no-show'));
  END IF;
END $$;

-- Add table_number column if it doesn't exist
DO $$ 
BEGIN
  IF NOT EXISTS (
    SELECT 1 FROM information_schema.columns 
    WHERE table_name = 'reservations' AND column_name = 'table_number'
  ) THEN
    ALTER TABLE reservations ADD COLUMN table_number TEXT;
  END IF;
END $$;

-- Add is_super_admin column to user_profiles
ALTER TABLE user_profiles 
ADD COLUMN IF NOT EXISTS is_super_admin BOOLEAN DEFAULT FALSE;

-- Create function to check if user is super admin
CREATE OR REPLACE FUNCTION is_super_admin()
RETURNS BOOLEAN
LANGUAGE plpgsql
SECURITY DEFINER
SET search_path = public
AS $$
DECLARE
  super_admin_status BOOLEAN;
BEGIN
  SELECT is_super_admin INTO super_admin_status
  FROM user_profiles
  WHERE user_id = auth.uid()
  LIMIT 1;

  RETURN COALESCE(super_admin_status, FALSE);
END;
$$;

-- Update RLS policies for user_profiles to allow super-admins to manage admin status
-- Drop existing admin policies and recreate with super-admin checks
DROP POLICY IF EXISTS "Admins can update all user profiles" ON user_profiles;
DROP POLICY IF EXISTS "Admins can insert user profiles" ON user_profiles;
DROP POLICY IF EXISTS "Admins can delete user profiles" ON user_profiles;

-- Super-admins can update all user profiles including admin status
CREATE POLICY "Super-admins can update all user profiles"
ON user_profiles FOR UPDATE
USING (
  (auth.uid() = id) OR is_super_admin()
);

-- Admins can update user profiles but not admin/super-admin status
CREATE POLICY "Admins can update user profiles"
ON user_profiles FOR UPDATE
USING (
  is_admin() AND NOT is_super_admin()
)
WITH CHECK (
  -- Prevent admins from changing admin or super_admin status
  is_admin = (SELECT is_admin FROM user_profiles WHERE id = user_profiles.id) AND
  is_super_admin = (SELECT is_super_admin FROM user_profiles WHERE id = user_profiles.id)
);

-- Super-admins can insert user profiles
CREATE POLICY "Super-admins can insert user profiles"
ON user_profiles FOR INSERT
WITH CHECK (
  (auth.uid() = id) OR is_super_admin()
);

-- Super-admins can delete user profiles
CREATE POLICY "Super-admins can delete user profiles"
ON user_profiles FOR DELETE
USING (is_super_admin());

-- Comment to document the role hierarchy
COMMENT ON COLUMN user_profiles.is_admin IS 'Admin role - can manage orders, menu, events, etc.';
COMMENT ON COLUMN user_profiles.is_super_admin IS 'Super Admin role - can manage admins and all admin functions';

-- Create a trigger to ensure super-admins always have admin privileges
CREATE OR REPLACE FUNCTION ensure_super_admin_is_admin()
RETURNS TRIGGER AS $$
BEGIN
  -- If user is being set as super admin, ensure they are also admin
  IF NEW.is_super_admin = TRUE THEN
    NEW.is_admin := TRUE;
  END IF;

  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- Create trigger for INSERT
DROP TRIGGER IF EXISTS ensure_super_admin_is_admin_insert ON user_profiles;
CREATE TRIGGER ensure_super_admin_is_admin_insert
  BEFORE INSERT ON user_profiles
  FOR EACH ROW
  EXECUTE FUNCTION ensure_super_admin_is_admin();

-- Create trigger for UPDATE
DROP TRIGGER IF EXISTS ensure_super_admin_is_admin_update ON user_profiles;
CREATE TRIGGER ensure_super_admin_is_admin_update
  BEFORE UPDATE ON user_profiles
  FOR EACH ROW
  EXECUTE FUNCTION ensure_super_admin_is_admin();

-- Update existing super-admins to ensure they have admin privileges
UPDATE user_profiles
SET is_admin = TRUE
WHERE is_super_admin = TRUE AND (is_admin IS NULL OR is_admin = FALSE);

-- Add index for better performance on role checks
CREATE INDEX IF NOT EXISTS idx_user_profiles_is_admin ON user_profiles(is_admin) WHERE is_admin = TRUE;
CREATE INDEX IF NOT EXISTS idx_user_profiles_is_super_admin ON user_profiles(is_super_admin) WHERE is_super_admin = TRUE;

-- Step 1: Add the new user_role column with a default value
ALTER TABLE user_profiles 
ADD COLUMN user_role TEXT DEFAULT 'user' CHECK (user_role IN ('user', 'admin', 'super_admin'));

-- Step 2: Migrate existing data
-- Set user_role based on existing is_super_admin and is_admin columns
UPDATE user_profiles 
SET user_role = CASE 
  WHEN is_super_admin = true THEN 'super_admin'
  WHEN is_admin = true THEN 'admin'
  ELSE 'user'
END;

-- Step 3: Make user_role NOT NULL now that all rows have values
ALTER TABLE user_profiles 
ALTER COLUMN user_role SET NOT NULL;

-- Step 4: Drop the old columns
ALTER TABLE user_profiles 
DROP COLUMN IF EXISTS is_admin,
DROP COLUMN IF EXISTS is_super_admin;

-- Step 5: Create or replace helper functions to check roles
CREATE OR REPLACE FUNCTION is_admin()
RETURNS BOOLEAN AS $$
BEGIN
  RETURN EXISTS (
    SELECT 1 
    FROM user_profiles 
    WHERE id = auth.uid() 
