import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

interface AddressValidationRequest {
  address: string;
}

interface AddressValidationResponse {
  success: boolean;
  isValid: boolean;
  formattedAddress?: string;
  addressComponents?: {
    streetNumber?: string;
    street?: string;
    city?: string;
    state?: string;
    postalCode?: string;
    country?: string;
  };
  confidence?: 'high' | 'medium' | 'low';
  suggestions?: string[];
  error?: string;
}

Deno.serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    // Verify authentication
    const authHeader = req.headers.get('Authorization');
    if (!authHeader) {
      throw new Error('Missing authorization header');
    }

    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_ANON_KEY') ?? '',
      {
        global: {
          headers: { Authorization: authHeader },
        },
      }
    );

    const {
      data: { user },
      error: userError,
    } = await supabaseClient.auth.getUser();

    if (userError || !user) {
      throw new Error('Unauthorized');
    }

    // Parse request body
    const { address }: AddressValidationRequest = await req.json();

    if (!address || address.trim().length === 0) {
      return new Response(
        JSON.stringify({
          success: false,
          isValid: false,
          error: 'Address is required',
        } as AddressValidationResponse),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 400,
        }
      );
    }

    // Get Google API key from environment
    const googleApiKey = Deno.env.get('GOOGLE_MAPS_API_KEY');

    if (!googleApiKey) {
      console.error('Google Maps API key not configured');
      // Fallback to basic validation if API key is not configured
      const basicValidation = performBasicValidation(address);
      return new Response(
        JSON.stringify(basicValidation),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 200,
        }
      );
    }

    // Call Google Address Validation API
    const googleResponse = await fetch(
      `https://addressvalidation.googleapis.com/v1:validateAddress?key=${googleApiKey}`,
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          address: {
            addressLines: [address],
          },
        }),
      }
    );

    if (!googleResponse.ok) {
      console.error('Google API error:', await googleResponse.text());
      // Fallback to basic validation
      const basicValidation = performBasicValidation(address);
      return new Response(
        JSON.stringify(basicValidation),
        {
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 200,
        }
      );
    }

    const googleData = await googleResponse.json();
    console.log('Google validation result:', JSON.stringify(googleData));

    // Parse Google's response
    const result = googleData.result;
    const verdict = result?.verdict;
    const address_obj = result?.address;

    // Determine validation confidence
    let confidence: 'high' | 'medium' | 'low' = 'low';
    let isValid = false;

    if (verdict) {
      // Google provides validation granularity
      const hasUnconfirmedComponents = verdict.hasUnconfirmedComponents || false;
      const hasInferredComponents = verdict.hasInferredComponents || false;
      const hasReplacedComponents = verdict.hasReplacedComponents || false;
      const validationGranularity = verdict.validationGranularity || 'OTHER';

      if (validationGranularity === 'PREMISE' && !hasUnconfirmedComponents) {
        confidence = 'high';
        isValid = true;
      } else if (validationGranularity === 'ROUTE' || (validationGranularity === 'PREMISE' && hasInferredComponents)) {
        confidence = 'medium';
        isValid = true;
      } else {
        confidence = 'low';
        isValid = false;
      }
    }

    // Extract formatted address
    const formattedAddress = address_obj?.formattedAddress || address;

    // Extract address components
    const addressComponents: AddressValidationResponse['addressComponents'] = {};
    if (address_obj?.addressComponents) {
      for (const component of address_obj.addressComponents) {
        const componentType = component.componentType;
        const componentName = component.componentName?.text || '';

        if (componentType === 'street_number') {
          addressComponents.streetNumber = componentName;
        } else if (componentType === 'route') {
          addressComponents.street = componentName;
        } else if (componentType === 'locality') {
          addressComponents.city = componentName;
        } else if (componentType === 'administrative_area_level_1') {
          addressComponents.state = componentName;
        } else if (componentType === 'postal_code') {
          addressComponents.postalCode = componentName;
        } else if (componentType === 'country') {
          addressComponents.country = componentName;
        }
      }
    }

    const response: AddressValidationResponse = {
      success: true,
      isValid,
      formattedAddress,
      addressComponents,
      confidence,
    };

    return new Response(JSON.stringify(response), {
      headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      status: 200,
    });
  } catch (error) {
    console.error('Address verification error:', error);
    return new Response(
      JSON.stringify({
        success: false,
        isValid: false,
        error: error.message || 'Failed to verify address',
      } as AddressValidationResponse),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 500,
      }
    );
  }
});

// Basic validation fallback when Google API is not available
function performBasicValidation(address: string): AddressValidationResponse {
  const trimmedAddress = address.trim();

  // Basic checks
  const hasNumber = /\d/.test(trimmedAddress);
  const hasStreet = trimmedAddress.split(/[,\n]/).length >= 2;
  const minLength = trimmedAddress.length >= 10;

  // Check for common address patterns
  const addressPatterns = [
    /\d+\s+[a-zA-Z\s]+/,  // Street number + street name
    /[a-zA-Z\s]+,\s*[a-zA-Z\s]+/,  // City, State pattern
    /\d{5}(-\d{4})?/,  // ZIP code
  ];

  const patternMatches = addressPatterns.filter(pattern => pattern.test(trimmedAddress)).length;

  let isValid = false;
  let confidence: 'high' | 'medium' | 'low' = 'low';

  if (hasNumber && hasStreet && minLength && patternMatches >= 2) {
    isValid = true;
    confidence = 'medium';
  } else if (hasNumber && minLength) {
    isValid = true;
    confidence = 'low';
  }

  return {
    success: true,
    isValid,
    formattedAddress: trimmedAddress,
    confidence,
  };
}

