import { serve } from "https://deno.land/std@0.168.0/http/server.ts";
import { createClient } from "https://esm.sh/@supabase/supabase-js@2";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers":
    "authorization, x-client-info, apikey, content-type",
};

interface PaymentRequest {
  sourceId: string;
  amount: number;
  currency: string;
  customer: {
    name: string;
    email: string;
    phone: string;
    address: string;
    city: string;
    state: string;
    zip: string;
  };
  items: Array<{
    id: string;
    name: string;
    quantity: number;
    price: number;
  }>;
}

serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === "OPTIONS") {
    return new Response("ok", { headers: corsHeaders });
  }

  try {
    // Get authorization header
    const authHeader = req.headers.get("Authorization");
    if (!authHeader) {
      return new Response(
        JSON.stringify({ error: "Missing authorization header" }),
        {
          status: 401,
          headers: { ...corsHeaders, "Content-Type": "application/json" },
        }
      );
    }

    // Initialize Supabase client
    const supabaseClient = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_ANON_KEY") ?? "",
      {
        global: {
          headers: { Authorization: authHeader },
        },
      }
    );

    // Get authenticated user
    const {
      data: { user },
      error: userError,
    } = await supabaseClient.auth.getUser();

    if (userError || !user) {
      console.error("User authentication error:", userError);
      return new Response(
        JSON.stringify({ error: "Unauthorized" }),
        {
          status: 401,
          headers: { ...corsHeaders, "Content-Type": "application/json" },
        }
      );
    }

    console.log("Processing payment for user:", user.id);

    // Parse request body
    const requestData: PaymentRequest = await req.json();
    const { sourceId, amount, currency, customer, items } = requestData;

    // Validate required fields
    if (!sourceId || !amount || !customer || !items || items.length === 0) {
      return new Response(
        JSON.stringify({ error: "Missing required fields" }),
        {
          status: 400,
          headers: { ...corsHeaders, "Content-Type": "application/json" },
        }
      );
    }

    // Generate idempotency key
    const idempotencyKey = crypto.randomUUID();

    // Process payment with Square
    const squareAccessToken = Deno.env.get("SQUARE_ACCESS_TOKEN");
    const squareEnvironment = Deno.env.get("SQUARE_ENVIRONMENT") || "sandbox";

    if (!squareAccessToken) {
      console.error("Square access token not configured");
      return new Response(
        JSON.stringify({ error: "Payment service not configured" }),
        {
          status: 500,
          headers: { ...corsHeaders, "Content-Type": "application/json" },
        }
      );
    }

    const squareBaseUrl =
      squareEnvironment === "production"
        ? "https://connect.squareup.com"
        : "https://connect.squareupsandbox.com";

    console.log("Calling Square API:", squareBaseUrl);

    const squareResponse = await fetch(`${squareBaseUrl}/v2/payments`, {
      method: "POST",
      headers: {
        "Square-Version": "2025-10-16",
        "Authorization": `Bearer ${squareAccessToken}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        source_id: sourceId,
        idempotency_key: idempotencyKey,
        amount_money: {
          amount: amount,
          currency: currency,
        },
        note: `Order from ${customer.name}`,
      }),
    });

    const squareData = await squareResponse.json();

    // Check if payment was successful
    if (!squareResponse.ok || !squareData.payment) {
      console.error("Square payment failed:", squareData);
      return new Response(
        JSON.stringify({
          error: squareData.errors?.[0]?.detail || "Payment processing failed",
          details: squareData.errors,
        }),
        {
          status: 400,
          headers: { ...corsHeaders, "Content-Type": "application/json" },
        }
      );
    }

    const payment = squareData.payment;
    console.log("Square payment successful:", payment.id);

    // Calculate total and points
    const totalAmount = amount / 100; // Convert cents to dollars
    const pointsEarned = Math.floor(totalAmount);

    // Use service role client for database operations
    const supabaseAdmin = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_SERVICE_ROLE_KEY") ?? ""
    );

    // Create square_payments record first
    const { data: paymentRecord, error: paymentError } = await supabaseAdmin
      .from("square_payments")
      .insert({
        user_id: user.id,
        square_payment_id: payment.id,
        amount: totalAmount,
        currency: currency,
        status: payment.status === "COMPLETED" ? "completed" : "pending",
        payment_method: payment.card_details?.card?.card_brand || "card",
        receipt_url: payment.receipt_url,
        metadata: {
          customer: customer,
          items: items,
        },
      })
      .select()
      .single();

    if (paymentError) {
      console.error("Failed to save payment record:", paymentError);
      return new Response(
        JSON.stringify({
          error: "Payment processed but failed to save record",
          paymentId: payment.id,
          warning: "Please contact support with this payment ID",
        }),
        {
          status: 500,
          headers: { ...corsHeaders, "Content-Type": "application/json" },
        }
      );
    }

    console.log("Payment record created:", paymentRecord.id);

    // Create order record
    const { data: orderRecord, error: orderError } = await supabaseAdmin
      .from("orders")
      .insert({
        user_id: user.id,
        total: totalAmount,
        points_earned: pointsEarned,
        status: "pending",
        delivery_address: `${customer.address}, ${customer.city}, ${customer.state} ${customer.zip}`,
        payment_id: paymentRecord.id,
      })
      .select()
      .single();

    if (orderError) {
      console.error("Failed to save order:", orderError);
      return new Response(
        JSON.stringify({
          error: "Payment processed but order save failed",
          paymentId: payment.id,
          warning: "Please contact support with this payment ID",
        }),
        {
          status: 500,
          headers: { ...corsHeaders, "Content-Type": "application/json" },
        }
      );
    }

    console.log("Order created:", orderRecord.id, "Order number:", orderRecord.order_number);

    // Update square_payments with order_id
    await supabaseAdmin
      .from("square_payments")
      .update({ order_id: orderRecord.id })
      .eq("id", paymentRecord.id);

    // Save order items
    const orderItemsData = items.map((item) => ({
      order_id: orderRecord.id,
      menu_item_id: parseInt(item.id),
      name: item.name,
      quantity: item.quantity,
      price: item.price,
    }));

    const { error: itemsError } = await supabaseAdmin
      .from("order_items")
      .insert(orderItemsData);

    if (itemsError) {
      console.error("Failed to save order items:", itemsError);
      // Don't fail the request, but log the error
    }

    // Update user points
    const { error: pointsError } = await supabaseAdmin
      .from("user_profiles")
      .update({
        points: supabaseAdmin.rpc("increment", { x: pointsEarned }),
      })
      .eq("id", user.id);

    if (pointsError) {
      console.error("Failed to update user points:", pointsError);
    }

    // Create notification for user
    await supabaseAdmin
      .from("notifications")
      .insert({
        user_id: user.id,
        title: "Order Placed Successfully",
        message: `Your order #${orderRecord.order_number} has been placed and is being prepared. You earned ${pointsEarned} points!`,
        type: "order",
        read: false,
      });

    console.log("Payment processing complete");

    // Return success response
    return new Response(
      JSON.stringify({
        success: true,
        orderId: orderRecord.id,
        orderNumber: orderRecord.order_number,
        paymentId: payment.id,
        orderStatus: payment.status,
        pointsEarned: pointsEarned,
        message: "Payment processed successfully",
      }),
      {
        status: 200,
        headers: { ...corsHeaders, "Content-Type": "application/json" },
      }
    );
  } catch (error) {
    console.error("Error processing payment:", error);
    return new Response(
      JSON.stringify({
        error: "Internal server error",
        message: error.message,
      }),
      {
        status: 500,
        headers: { ...corsHeaders, "Content-Type": "application/json" },
      }
    );
  }
});

