import { createClient } from "npm:@supabase/supabase-js@2.46.1";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
  "Access-Control-Allow-Methods": "POST, OPTIONS",
};

interface PaymentRequest {
  sourceId: string;
  amount: number; // cents
  currency?: string; // defaults to USD
  customer: {
    name: string;
    email: string;
    phone?: string;
    address?: string;
    city?: string;
    state?: string;
    zip?: string;
  };
  items: Array<{
    id: string; // menu_item_id numeric string
    name: string;
    quantity: number;
    price: number; // dollars
  }>;
  orderType?: 'delivery' | 'pickup';
  deliveryAddress?: string;
  pickupNotes?: string;
}

function json(payload: unknown, status = 200) {
  return new Response(JSON.stringify(payload), {
    status,
    headers: { ...corsHeaders, "Content-Type": "application/json" },
  });
}

function looksLikeAnonKey(header: string | null) {
  if (!header) return false;
  const m = header.match(/^Bearer\s+(.+)$/i);
  if (!m) return false;
  const token = m[1];
  const envAnon = Deno.env.get("SUPABASE_ANON_KEY");
  if (envAnon && token === envAnon) return true;
  if (/anon/i.test(token)) return true;
  return false;
}

console.info("process-square-payment server started");
Deno.serve(async (req: Request) => {
  if (req.method === "OPTIONS") return new Response("ok", { headers: corsHeaders });

  try {
    const authHeader = req.headers.get("Authorization");
    const hasBearer = !!authHeader && authHeader.toLowerCase().startsWith("bearer ");
    const skipUserLookup = looksLikeAnonKey(authHeader);

    const supabaseUser = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_ANON_KEY") ?? "",
      hasBearer && !skipUserLookup ? { global: { headers: { Authorization: authHeader! } } } : undefined
    );

    const supabaseAdmin = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_SERVICE_ROLE_KEY") ?? ""
    );

    let userId: string | null = null;
    if (hasBearer && !skipUserLookup) {
      const { data, error } = await supabaseUser.auth.getUser();
      if (data?.user && !error) userId = data.user.id;
    }

    const body: PaymentRequest = await req.json();
    const { sourceId, amount, currency = "USD", customer, items, orderType = 'delivery', deliveryAddress, pickupNotes } = body;

    // Validate inputs against DB schema expectations
    if (!sourceId || typeof sourceId !== "string") return json({ error: "Invalid sourceId" }, 400);
    if (!Number.isInteger(amount) || amount <= 0) return json({ error: "Invalid amount (cents)" }, 400);
    if (!Array.isArray(items) || items.length === 0) return json({ error: "No items" }, 400);

    // Prepare totals consistent with DB numeric columns
    const totalDollars = +(amount / 100).toFixed(2); // numeric for orders.total and square_payments.amount

    // Charge with Square
    const idempotencyKey = crypto.randomUUID();
    const squareAccessToken = Deno.env.get("SQUARE_ACCESS_TOKEN");
    const squareEnvironment = Deno.env.get("SQUARE_ENVIRONMENT") || "sandbox";
    if (!squareAccessToken) return json({ error: "Payment service not configured" }, 500);
    const squareBaseUrl = squareEnvironment === "production"
      ? "https://connect.squareup.com"
      : "https://connect.squareupsandbox.com";

    const resp = await fetch(`${squareBaseUrl}/v2/payments`, {
      method: "POST",
      headers: {
        "Square-Version": "2024-08-15",
        "Authorization": `Bearer ${squareAccessToken}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        source_id: sourceId,
        idempotency_key: idempotencyKey,
        amount_money: { amount, currency },
        note: customer?.name ? `${orderType === 'pickup' ? 'Pickup' : 'Delivery'} order from ${customer.name}` : undefined,
      }),
    });

    const squareData = await resp.json();
    if (!resp.ok || !squareData?.payment) {
      return json({ error: squareData?.errors?.[0]?.detail || "Payment failed", details: squareData?.errors }, 400);
    }

    const payment = squareData.payment;
    const statusMap: Record<string, "pending" | "completed" | "failed" | "refunded" | "cancelled"> = {
      COMPLETED: "completed",
      APPROVED: "pending",
      CANCELED: "cancelled",
      FAILED: "failed",
      REFUNDED: "refunded",
    };
    const normalizedStatus = statusMap[payment.status as string] || "pending";

    // Insert square_payments with exact column names/types
    const insertPayment = {
      user_id: userId, // uuid nullable (we pass null for guest)
      order_id: null as string | null, // filled after order insert
      square_payment_id: payment.id as string,
      square_order_id: payment.order_id ?? null,
      amount: totalDollars, // numeric
      currency, // text defaults to USD
      status: normalizedStatus, // matches check constraint
      payment_method: payment.card_details?.card?.card_brand ?? null,
      receipt_url: payment.receipt_url ?? null,
      error_message: null as string | null,
      metadata: { customer, items, orderType } as unknown, // jsonb
    };

    const { data: paymentRow, error: payErr } = await supabaseAdmin
      .from("square_payments")
      .insert(insertPayment)
      .select("id, status, receipt_url")
      .single();

    if (payErr) {
      return json({ error: "Failed to save payment", details: payErr }, 400);
    }

    // Create order
    const pointsEarned = userId ? Math.floor(totalDollars) : 0;

    // Use deliveryAddress or pickupNotes based on orderType
    const finalDeliveryAddress = orderType === 'delivery' ? deliveryAddress : null;
    const finalPickupNotes = orderType === 'pickup' ? pickupNotes : null;

    const { data: orderRow, error: orderErr } = await supabaseAdmin
      .from("orders")
      .insert({
        user_id: userId, // uuid
        total: totalDollars, // numeric
        points_earned: pointsEarned, // int
        status: "pending", // check constraint compatible
        delivery_address: finalDeliveryAddress,
        pickup_notes: finalPickupNotes,
        payment_id: paymentRow.id, // uuid -> FK to square_payments.id
      })
      .select("id, order_number")
      .single();

    if (orderErr) {
      // best-effort rollback link: update payment with error and leave it recorded
      await supabaseAdmin
        .from("square_payments")
        .update({ error_message: "Order insert failed" })
        .eq("id", paymentRow.id);
      return json({ error: "Failed to save order", details: orderErr }, 400);
    }

    // Backfill order_id on payment
    await supabaseAdmin
      .from("square_payments")
      .update({ order_id: orderRow.id })
      .eq("id", paymentRow.id);

    // Insert order_items (numeric price)
    const orderItemsData = items.map((i) => ({
      order_id: orderRow.id,
      menu_item_id: isNaN(Number(i.id)) ? null : Number(i.id),
      name: i.name,
      quantity: i.quantity,
      price: +(+i.price).toFixed(2),
    }));

    const { error: itemsErr } = await supabaseAdmin.from("order_items").insert(orderItemsData);
    if (itemsErr) {
      await supabaseAdmin
        .from("square_payments")
        .update({ error_message: "Order items insert failed" })
        .eq("id", paymentRow.id);
    }

    // Loyalty side effects (optional)
    if (userId && pointsEarned > 0) {
      const { error: pointsErr } = await supabaseAdmin.rpc("increment_user_points", {
        user_id_param: userId,
        points_to_add: pointsEarned,
      });
      if (pointsErr) {
        await supabaseAdmin
          .from("square_payments")
          .update({ error_message: "Points update failed" })
          .eq("id", paymentRow.id);
      }

      const orderTypeText = orderType === 'pickup' ? 'pickup' : 'delivery';
      await supabaseAdmin.from("notifications").insert({
        user_id: userId,
        title: "Order Placed Successfully",
        message: `Your ${orderTypeText} order #${orderRow.order_number} has been placed. You earned ${pointsEarned} points!`,
        type: "order",
        read: false,
      });
    }

    return json({
      success: true,
      orderId: orderRow.id,
      orderNumber: orderRow.order_number,
      paymentId: payment.id,
      orderStatus: payment.status,
      pointsEarned,
      mode: userId ? "user" : "guest",
      skipped_user_lookup: skipUserLookup,
    });
  } catch (err: any) {
    return json({ error: "Internal server error", message: err?.message }, 500);
  }
});

